import React from "react";
import styles from "./HomePage1.module.css";
import wallpaper from "../../images/wallpaper.png";
import PersonAddAltIcon from "@mui/icons-material/PersonAddAlt";
import LoginIcon from "@mui/icons-material/Login";
import Button from "@mui/material/Button";
import { IconButton } from "@mui/material";
import { useState } from "react";

function HomePage1() {


  const [hoveredButton, setHoveredButton] = useState(null);

  const handleMouseEnter = (buttonName) => {
    setHoveredButton(buttonName);
  };

  const handleMouseLeave = () => {
    setHoveredButton(null);
  };

  const isButtonHovered = (buttonName) => {
    return hoveredButton === buttonName;
  };

  return (
    <div
      className={styles.imageContainer}
      style={{
        width: "100%",
        height: "100vh",
        backgroundImage: `url(${wallpaper})`,
        backgroundSize: "100% 100%",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
      }}
    >
      <div className={styles.buttonContainer}>
        <Button
          style={{
            color: "white",
            border: "1px solid white",
            marginRight: "30px",
            width: "200px",
            height: "100px",
            background: isButtonHovered("signup") ? "lightgray" : "transparent",
            boxShadow: isButtonHovered("signup") ? "0px 2px 4px rgba(0, 0, 0, 0.2)" : "none",
          }}
          startIcon={<PersonAddAltIcon />}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          Sign Up
        </Button>
        <Button
          style={{
            color: "white",
            border: "1px solid white",
            marginRight: "30px",
            width: "200px",
            height: "100px",
            background: isButtonHovered("login") ? "lightgray" : "transparent",
            boxShadow: isButtonHovered("login") ? "0px 2px 4px rgba(0, 0, 0, 0.2)" : "none",
          }}
          startIcon={<LoginIcon />}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          Log In
        </Button>
        <Button
          style={{
            color: "white",
            border: "1px solid white",
            width: "200px",
            height: "100px",
            background: isButtonHovered("guest") ? "lightgray" : "transparent",
            boxShadow: isButtonHovered("guest") ? "0px 2px 4px rgba(0, 0, 0, 0.2)" : "none",
          }}
          startIcon={<LoginIcon />}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          Play as Guest
        </Button>
      </div>
    </div>
  );
}

export default HomePage1;
